document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("menu"),t=document.querySelector("body"),o=document.querySelectorAll(".nav-item-button"),l=document.querySelector(".bi-list"),n=document.getElementById("menu-modal"),d=document.getElementById("close");e.addEventListener("click",(()=>{l.classList.contains("bi-list")?(l.style.opacity="0",n.style.display="block",t.style.overflowY="hidden"):(l.style.opacity="1",n.style.display="none",t.style.overflow="auto")})),d.addEventListener("click",(()=>{l.style.opacity="1",n.style.display="none",t.style.overflow="auto"})),o.forEach((e=>{e.addEventListener("click",(()=>{l.style.opacity="1",n.style.display="none",t.style.overflowY="auto"}))}))}));